import { Component, EventEmitter, Input, Output } from "@angular/core";
import { FaIconLibrary } from "@fortawesome/angular-fontawesome";
import { fas } from "@fortawesome/free-solid-svg-icons";

@Component({
  selector: "app-button",
  templateUrl: "./button.component.html",
  styleUrls: ["./button.component.scss"],
})
export class ButtonComponent {
  constructor(library: FaIconLibrary) {
    library.addIconPacks(fas);
  }

  // Use the names for the inputs `buttonText` and `iconName`.
  @Input() buttonText?: string;
  @Input() iconName?: any;
  @Input() reversed: boolean = false;

  @Output() changeLoggedStatus = new EventEmitter<void>();

  changeLoginStatus() {
    this.changeLoggedStatus.emit();
  }
}
<div class="app-button" *ngIf="buttonText || iconName" (click)="changeLoginStatus()" [ngStyle]="reversed ? { 'background-color': '#fff', 'color': '#007298', 'font-size': '30px' } : { 'background-color': '#007298', 'color': '#fff'}" >
  <!-- Replace the text below with your code -->
   <ng-container *ngIf="buttonText; else icon">
    {{ buttonText | uppercase }}
   </ng-container>

   <ng-template #icon>
     <fa-icon *ngIf="iconName" [icon]="iconName"></fa-icon>
   </ng-template>
  
</div>
