@Component({
  selector: 'app-info',
  templateUrl: './info.component.html',
  styleUrls: ['./info.component.scss']
})
export class InfoComponent {
  @Input() title!: string;  
  @Input() text?: string; 
}  
// Use the names `title` and `text`.
<div class="app-info">
  <!-- Replace ::text:: with your code -->
  <div class="app-info__header">::Title::</div>
  <div class="app-info__title">::Text::</div>
</div>
