import { Component } from "@angular/core";
import { Post, posts } from "./shared/mocks";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.scss"],
})
export class AppComponent {
  title = "courses-app";

  public loginStatus: boolean = false;
  public posts: Post[] = posts;

  changeLoginStatus() {
    this.loginStatus = !this.loginStatus;
  }
}
<app-header>
  <div class="header-right">
    <div *ngIf="loginStatus" class="user-name">Harry Potter</div>
    <app-button
      [buttonText]="loginStatus ? 'logout' : 'login'"
      (changeLoggedStatus)="changeLoginStatus()"
    ></app-button>
  </div>
</app-header>
<div class="app-root">
  <!-- Search component -->
  <!--<app-search></app-search>-->

  <app-info [text]="'Please use &#8217;Add New Course&#8217; button to add your first course'" [title]="'Your List Is Empty'">
    <app-button [buttonText]="'add new course'"></app-button>
  </app-info>

  <!-- Course Card component -->
  <!-- <app-course-card></app-course-card> -->

  <app-courses-list [editable]="true" [courses]="posts"></app-courses-list>

  <app-course-info [course]="posts[0]"></app-course-info>

  <!-- Login Form component -->
  <!--<app-login-form></app-login-form>-->

  <!-- Login Form component -->
  <!--<app-registration-form></app-registration-form>-->

  <!-- Login Form component -->
  <!--<app-course-form></app-course-form>-->

  <!-- Modal component -->
  <!-- <app-modal [message]="'Message'" [title]="'Title'" [okButtonText]="'Ok'" [cancelButtonText]="'Cancel'"></app-modal> -->
</div>
