import { Component, EventEmitter, Input, Output } from "@angular/core";
import { Post } from "@app/shared/mocks";

@Component({
  selector: "app-course-card",
  templateUrl: "./course-card.component.html",
  styleUrls: ["./course-card.component.scss"],
})
export class CourseCardComponent {
  math = Math;
  @Input() course: Post | null = null;
  @Input() editable: boolean = false;
  @Output() showCourse = new EventEmitter();
  @Output() editCourse = new EventEmitter();
  @Output() deleteCourse = new EventEmitter();
}
<div class="course-card" *ngIf="course">
    <div class="course-card__description">
      <!-- Replace ::text:: with your code -->
      <div class="course-card__description-title">{{course.title}}</div>
      <div class="course-card__description-text">{{course.description}}</div>
    </div>
    <div class="course-card__info">
        <div class="course-card__info-text">
            <div>
              <b>Authors: </b>
              {{course.authors}}
            </div>
            <div><b>Duration: </b><span class="course-card__duration">{{math.trunc(course.duration/60)}}:{{course.duration%60 === 0 ? "00" : course.duration%60}} hours</span></div>
            <div><b>Created: </b><span class="course-card__date">{{course.creationDate.getDay().toString().padStart(2,"0")}}.{{course.creationDate.getMonth().toString().padStart(2,"0")}}.{{course.creationDate.getFullYear()}}</span></div>
        </div>
        <div class="course-card__info-actions">
            <app-button [buttonText]="'show course'"></app-button>
            <div class="course-card__buttons" *ngIf="editable">
                <app-button [iconName]="'trash-can'" [reversed]="true"></app-button>
                <app-button [iconName]="'pencil'" [reversed]="true"></app-button>
            </div>
        </div>
    </div>
</div>
